// src/components/CurrentWeatherCard.jsx
import React from 'react';

export default function CurrentWeatherCard({ data }) {
  // 1️⃣ If we don’t have currentConditions, bail out:
  if (!data || !data.currentConditions) {
    return null;
  }

  const cond = data.currentConditions;
  const icon = cond.weatherCondition.iconUri;
  // Pull the text field off of the description object:
  const description = cond.weatherCondition.description.text;
  const temp = cond.temperature.temperature;
  const feelsLike = cond.temperature.feelsLike;
  const humidity = cond.relativeHumidity;
  const windSpeed = cond.wind.speed;

  return (
    <div className="bg-white p-6 rounded-lg shadow w-full max-w-md mb-4">
      <div className="flex items-center justify-between">
        <img src={icon} alt={description} className="h-20 w-20" />
        <div className="text-5xl font-bold">{Math.round(temp)}°</div>
      </div>
      <div className="capitalize text-lg mt-2">{description}</div>
      <div className="mt-4 text-sm text-gray-600">
        Feels like {Math.round(feelsLike)}° • Humidity {humidity}% • Wind {windSpeed} m/s
      </div>
    </div>
  );
}
