import React, { useContext, useEffect, useState } from 'react';
import WeatherContext from './context/WeatherContext';
import useGeolocation from './hooks/useGeolocation';
import { useWeather } from './hooks/useWeather';
import SearchBar from './components/SearchBar';
import CurrentWeatherCard from './components/CurrentWeatherCard';
import ForecastList from './components/ForecastList';
import Loader from './components/Loader';
import Error from './components/Error';

export default function App() {
  const { city, setCity, addToHistory, history } = useContext(WeatherContext);
  const [units, setUnits] = useState('IMPERIAL');
  const geo = useGeolocation();

  // Build query params: prefer manual city over geolocation
  const queryParams = city.trim()
    ? { city }
    : geo.lat != null && geo.lon != null
    ? { geo: { lat: geo.lat, lon: geo.lon } }
    : {};

  const { current, daily, hourly, loading, error } =
    useWeather({ ...queryParams, units });

  useEffect(() => {
    if (city && current) addToHistory(city);
  }, [city, current, addToHistory]);

  // Clears city to trigger geolocation-based fetch
  const handleUseGeo = () => setCity('');

  return (
    <div className="min-h-screen flex flex-col items-center p-4">
      <h1 className="text-3xl font-bold mb-4">Weather App</h1>

      <div className="w-full max-w-md flex space-x-2 mb-4">
        <SearchBar onSearch={setCity} onUseGeo={handleUseGeo} />
        <select
          value={units}
          onChange={(e) => setUnits(e.target.value)}
          className="p-2 border rounded"
        >
          <option value="METRIC">°C</option>
          <option value="IMPERIAL">°F</option>
        </select>
      </div>

      {loading || geo.loading ? (
        <Loader />
      ) : error || geo.error ? (
        <Error message={error || geo.error} />
      ) : current ? (
        <>
          <CurrentWeatherCard data={current} units={units} />
          <ForecastList daily={daily} units={units} />
        </>
      ) : (
        <div className="text-gray-600">Enter a city or use your location above.</div>
      )}

      {history.length > 0 && (
        <div className="mt-4">
          <h2 className="font-semibold">Recent:</h2>
          <ul className="flex space-x-2">
            {history.map((item) => (
              <li
                key={item}
                className="cursor-pointer underline"
                onClick={() => setCity(item)}
              >
                {item}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
