// src/hooks/useWeather.js
import { useState, useEffect } from 'react';
import {
  geocodeCity,
  fetchCurrentByCoords,
  fetchDailyForecastByCoords,
  fetchHourlyForecastByCoords,
} from '../api/weather';

/**
 * @param {{
 *   city?: string,
 *   geo?: { lat: number, lon: number },
 *   units?: 'METRIC' | 'IMPERIAL'
 * }} options
 */
export function useWeather({ city = null, geo = null, units = 'METRIC' } = {}) {
  const [current, setCurrent] = useState(null);
  const [daily,   setDaily]   = useState(null);
  const [hourly,  setHourly]  = useState(null);
  const [loading, setLoading] = useState(false);
  const [error,   setError]   = useState(null);

  useEffect(() => {
    let cancelled = false;

    async function loadWeather() {
      setLoading(true);
      setError(null);

      try {
        // 1️⃣ Resolve coordinates
        const coords = city
          ? await geocodeCity(city)
          : { lat: geo.lat, lon: geo.lon };

        // 2️⃣ Fetch everything in parallel, passing the chosen units
        const [curRes, dayRes, hrRes] = await Promise.all([
          fetchCurrentByCoords(      coords, units),
          fetchDailyForecastByCoords(coords, 5,    5,    units),
          fetchHourlyForecastByCoords(coords, 24,  24,   units),
        ]);

        if (!cancelled) {
          setCurrent(curRes);                           // { currentConditions, metadata }
          setDaily(  dayRes.forecastDays   || dayRes);  // array of day forecasts
          setHourly( hrRes.forecastHours  || hrRes);    // array of hour forecasts
        }
      } catch (err) {
        if (!cancelled) setError(err.message);
      } finally {
        if (!cancelled) setLoading(false);
      }
    }

    // Only fire when we have a city or geo coords
    if (
      (city && city.trim()) ||
      (geo?.lat != null && geo?.lon != null)
    ) {
      loadWeather();
    }

    return () => {
      cancelled = true;
    };
  }, [city, geo, units]);   // ← watch units, too!

  return { current, daily, hourly, loading, error };
}
