//oads current & forecast when city changes
import {useState, useEffect} from 'react';
import { 
    fetchCurrentByCoords,
    fetchDailyForecastByCoords,
    fetchHourlyForecastByCoords,
    geocodeCity 
} from '../api/weather';

//Hook to fetch current, daily, and hourly data either by city name or coordinates
export function useWeather({ city = null, geo = null } = {}) {
  const [current, setCurrent] = useState(null);
  const [daily, setDaily]     = useState(null);
  const [hourly, setHourly]   = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError]     = useState(null);

  useEffect(() => {
    let cancelled = false;

    async function loadWeather() {
      setLoading(true);
      setError(null);

      try {
        //Determine coords
        const coords = city
          ? await geocodeCity(city)
          : { lat: geo.lat, lon: geo.lon };

        //Fetch current, daily & hourly in parallel
        const [curRes, dayRes, hrRes] = await Promise.all([
          fetchCurrentByCoords(coords),
          fetchDailyForecastByCoords(coords),
          fetchHourlyForecastByCoords(coords),
        ]);

        if (!cancelled) {
          setCurrent(curRes);
          setDaily(dayRes.forecastDays || dayRes);
          setHourly(hrRes.forecastHours || hrRes);
        }
      } catch (err) {
        if (!cancelled) setError(err.message);
      } finally {
        if (!cancelled) setLoading(false);
      }
    }

    //Only trigger if we have either a non‐empty city or valid geo coords
    if (
      (city && city.trim().length > 0) ||
      (geo && typeof geo.lat === 'number' && typeof geo.lon === 'number')
    ) {
      loadWeather();
    }

    return () => {
      cancelled = true;
    };
  }, [city, geo]);

  return { current, daily, hourly, loading, error };
}
